// [1] å–å¾— AI Node ä¸Šæ¸¸çš„ output
let rawContent = $input.first().json.output;

// [2] å˜—è©¦è§£ææ–‡å­—å…§å®¹ï¼šå¦‚æœå·²ç¶“æ˜¯é™£åˆ—å°±ç›´æ¥ç”¨ï¼Œå¦‚æœæ˜¯æ–‡å­—å‰‡æ­£è¦èƒå–é™£åˆ—
let questionsArr;

if (typeof rawContent === 'string') {
  const jsonMatch = rawContent.match(/\[\s*{.*}\s*\]/s);
  if (!jsonMatch) {
    throw new Error('åœ¨ AI å›è¦†ä¸­æ‰¾ä¸åˆ°æœ‰æ•ˆçš„ JSON é™£åˆ—ã€‚æ”¶åˆ°å…§å®¹: ' + rawContent);
  }
  try {
    questionsArr = JSON.parse(jsonMatch[0]);
  } catch (err) {
    throw new Error('AI å›è¦†ä¸æ˜¯æ­£ç¢ºçš„ JSON æ ¼å¼ï¼š' + err.message);
  }
} else if (Array.isArray(rawContent)) {
  questionsArr = rawContent;
} else {
  throw new Error('AI Node output å¿…é ˆæ˜¯å­—ä¸²(å«é™£åˆ—)æˆ–ç›´æ¥ç‚ºé™£åˆ—');
}

// [3] å–å¾—èˆŠé¡Œåº«ï¼ˆå»ºè­°ä½ çš„ workflow æœ‰å€‹å‰ç½®ç¯€é»è™•ç†ç¾æœ‰é¡Œåº«ï¼‰
const existingData = $('è§£æç¾æœ‰é¡Œåº«ä¸¦ç²å–æœ€å¤§é¡Œè™Ÿ2').first().json || {};
const existingQuestions = existingData.existingQuestions || [];
const maxNumber = existingData.maxNumber || 0;

// [4] ç”Ÿæˆæ—¥æœŸ
const today = new Date().toISOString().split('T')[0];

// [5] è™•ç†æ–°é¡Œç›®ï¼šè£œç·¨è™Ÿã€æ—¥æœŸ
const processedNewQuestions = questionsArr.map((q, idx) => ({
  ...q,
  number: maxNumber + idx + 1,
  date: today
}));

// [6] æº–å‚™å¯«å…¥ Google Sheets
const dataForSheet = processedNewQuestions.map((q) => ({
  date: q.date,
  number: q.number,
  question: q.question,
  options: JSON.stringify(q.options), // å­—ä¸²åŒ–
  correct_answer_letter: q.correct_answer_letter,
  explanation: q.explanation,
  topic: q.topic,
  subtopic: q.subtopic,
  concept: q.concept
}));

// [7] åˆä½µèˆŠé¡Œåº«èˆ‡æ–°é¡Œç›®ï¼ˆGitHub ç”¨ï¼‰
const allQuestions = [...existingQuestions, ...processedNewQuestions];

// [8] æº–å‚™ LINE æ¨é€æ–‡å­—
let textForLine = `ğŸ’¡ ä»Šæ—¥ä»½çš„ iPAS AI æ‡‰ç”¨è¦åŠƒå¸«ç·´ç¿’é¡Œ ğŸ’¡\n\n`;
processedNewQuestions.forEach((q) => {
  textForLine += `ç¬¬ ${q.number} é¡Œ: ${q.question}\n`;
  q.options.forEach((opt, i) => {
    const letter = String.fromCharCode(65 + i);
    textForLine += `  (${letter}) ${opt}\n`;
  });
  textForLine += '\n';
});
textForLine += 'è«‹ä¾ç…§æ ¼å¼å›è¦†ç­”æ¡ˆï¼Œä¾‹å¦‚ï¼š1A2B3C...';

// [9] è¼¸å‡ºæ¯å€‹ itemï¼ˆfor Google Sheetsï¼‰ï¼Œæ¯ item éƒ½å¸¶å®Œæ•´é¡Œåº«ï¼ˆGitHub ç”¨ï¼‰
return dataForSheet.map(item => ({
  json: {
    ...item,
